window.addEventListener("resize", windowResize);
$(document).ready(function() {windowResize();});
var widthChat = 640;
var widthStreams = 100;
function windowResize() {
	var width = $( window ).width();
	var height = $( window ).height();
	widthStreams = width - widthChat - 10;
	$("#chat").width(widthChat);
	$("#streams").width(widthStreams);
	$("#streams").height(height - 53);
	$("#chat").height(height - 33);
	$("#ChatBox").height(height - 53);
	$("#streamContent").height(height - 107);
}
function showHitboxResult(t){if(t.length==0){document.getElementById("hitboxSearch").innerHTML=""; xmlhttp.abort(); document.getElementById("hitboxSearch").style.border = "0px";document.getElementById("hitboxSearch").style.height = "0em";;return}if(t.length<2){xmlhttp.abort();document.getElementById("hitboxSearch").innerHTML="Please enter 2 or more letters";document.getElementById("hitboxSearch").style.height = "5em";document.getElementById("hitboxSearch").style.overflowY = "hidden";document.getElementById("hitboxSearch").style.border="1px solid #330000";return}else{if(window.XMLHttpRequest){xmlhttp=new XMLHttpRequest}else{xmlhttp=new ActiveXObject("Microsoft.XMLHTTP")}xmlhttp.onreadystatechange=function(){if(xmlhttp.readyState==4&&xmlhttp.status==200){document.getElementById("hitboxSearch").innerHTML=xmlhttp.responseText; document.getElementById("hitboxSearch").style.border = "1px solid #330000";document.getElementById("hitboxSearch").style.overflowY = "scroll";document.getElementById("hitboxSearch").style.height = "11em";}};xmlhttp.open("GET","mg/api/search/hitboxSearch.php?q="+t,true);xmlhttp.send()}}
function showTwitchResult(t){if(t.length==0){document.getElementById("twitchSearch").innerHTML="";xmlhttp.abort();;document.getElementById("twitchSearch").style.border="0px";document.getElementById("twitchSearch").style.height = "0em";return}if(t.length<2){xmlhttp.abort();document.getElementById("twitchSearch").innerHTML="Please enter 2 or more letters";document.getElementById("twitchSearch").style.border = "1px solid #330000";document.getElementById("twitchSearch").style.overflowY = "hidden";document.getElementById("twitchSearch").style.height = "5em";;return}else{if(window.XMLHttpRequest){xmlhttp=new XMLHttpRequest}else{xmlhttp=new ActiveXObject("Microsoft.XMLHTTP")}xmlhttp.onreadystatechange=function(){if(xmlhttp.readyState==4&&xmlhttp.status==200){document.getElementById("twitchSearch").innerHTML=xmlhttp.responseText;document.getElementById("twitchSearch").style.border = "1px solid #330000";document.getElementById("twitchSearch").style.overflowY = "scroll";document.getElementById("twitchSearch").style.height = "11em";}};xmlhttp.open("GET","mg/api/search/twitchSearch.php?q="+t,true);xmlhttp.send()}}
function allowDrop(t){t.preventDefault()}
function drag(t){t.dataTransfer.setData("Text",t.target.id)}
function drop(t){t.preventDefault();var e=t.dataTransfer.getData("Text");t.target.appendChild(document.getElementById(e))}
function addToHitboxList(t){var tnew = $("#hitbox").val().concat(t,",");$("#hitbox").val(tnew);}
function addToTwitchList(t){var tnew = $("#twitch").val().concat(t,",");$("#twitch").val(tnew);}
