function showHitboxResult(t){if(t.length==0){document.getElementById("hitboxSearch").innerHTML="";document.getElementById("hitboxSearch").style.border="0px";return}if(t.length<2){document.getElementById("hitboxSearch").innerHTML="Please enter 2 or more letters";document.getElementById("hitboxSearch").style.border="1px solid #003300";return}else{if(window.XMLHttpRequest){xmlhttp=new XMLHttpRequest}else{xmlhttp=new ActiveXObject("Microsoft.XMLHTTP")}xmlhttp.onreadystatechange=function(){if(xmlhttp.readyState==4&&xmlhttp.status==200){document.getElementById("hitboxSearch").innerHTML=xmlhttp.responseText;document.getElementById("hitboxSearch").style.border="1px solid #003300"}};xmlhttp.open("GET","multigaming/api/search/hitboxSearch.php?q="+t,true);xmlhttp.send()}}function showTwitchResult(t){if(t.length==0){document.getElementById("twitchSearch").innerHTML="";document.getElementById("twitchSearch").style.border="0px";return}if(t.length<2){document.getElementById("twitchSearch").innerHTML="Please enter 2 or more letters";document.getElementById("twitchSearch").style.border="1px solid #003300";return}else{if(window.XMLHttpRequest){xmlhttp=new XMLHttpRequest}else{xmlhttp=new ActiveXObject("Microsoft.XMLHTTP")}xmlhttp.onreadystatechange=function(){if(xmlhttp.readyState==4&&xmlhttp.status==200){document.getElementById("twitchSearch").innerHTML=xmlhttp.responseText;document.getElementById("twitchSearch").style.border="1px solid #003300"}};xmlhttp.open("GET","multigaming/api/search/twitchSearch.php?q="+t,true);xmlhttp.send()}}function allowDrop(t){t.preventDefault()}function drag(t){t.dataTransfer.setData("Text",t.target.id)}function drop(t){t.preventDefault();var e=t.dataTransfer.getData("Text");t.target.appendChild(document.getElementById(e))}